INSERT INTO `tbl_content` VALUES ('7', 'a:3:{s:6:\"header\";s:0:\"\";s:5:\"autor\";s:0:\"\";s:7:\"content\";s:4250:\"     <h1 class=\"title\">        	      	                      <span class=\"post_title\">Carmageddon для iOS и Android</span>                      			          	</h1>   	   	 <div class=\"hubs\"> 	 		<a href=\"http://habrahabr.ru/hub/iphone/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >iPhone</a>,  	 		<a href=\"http://habrahabr.ru/hub/android/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Android</a><span class=\"profiled_hub\" title=\"Профильный хаб\">*</span>,  	 		<a href=\"http://habrahabr.ru/company/apps4all/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Блог компании «Apps4All»</a> 	 </div>    	   	<div class=\"content html_format\">   	Переиздание Carmageddon, без преувеличения культовой игры 90-ых годов, готовится для мобильных платформ. <br/>  <br/>  Игра собрала более 500 000 долларов на KickStarter и уже этим летом должна появиться на iOS и Android (хотя, конечно, странно как Apple пропустила в магазин игру, в которой надо давить людей). По словам создателей из Stainless Games, игра будет сразу поставляться с расширением Splat Pack. <br/>  <br/>  <iframe width=\"560\" height=\"349\" src=\"http://www.youtube.com/embed/jKjEfS0IRT8\" frameborder=\"0\" allowfullscreen></iframe>    	   	<div class=\"clear\"></div> 	   	</div>      <ul class=\"tags\"> 	<li ><a href=\"http://habrahabr.ru/search/?q=%5B%D0%B8%D0%B3%D1%80%D0%B0%5D&target_type=posts\" rel=\"tag\">игра</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5BiOS%5D&target_type=posts\" rel=\"tag\">iOS</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5Bandroid%5D&target_type=posts\" rel=\"tag\">android</a></li> </ul>      <div class=\"infopanel\" id=\"infopanel_post_145155\"> 	          	 	<div class=\"voting\">  		 		 		<span class=\"plus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		<span class=\"minus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		  		<div class=\"mark\"> 			 			 			 			 			<span class=\"score\" title=\"Результат голосования виден только авторизованным пользователям\">&#8212;</span> 			 			 		</div>  	</div> 	 	    	<div class=\"published\"> 4 июня 2012, 13:53</div>  	<div class=\"favorite\"> 		 		 		<a class=\"guest\"  href=\"#\" onclick=\"return true;\" title=\"Только зарегистрированные пользователи могут добавлять посты в избранное\"></a> 		 	</div>  	<div class=\"favs_count\" title=\"Количество пользователей, добавивших пост в избранное\">17</div>  	 		 		 		<div class=\"twitter\"> 			<a href=\"http://twitter.com/intent/tweet?text=http://habr.ru/p/145155/+Carmageddon+%D0%B4%D0%BB%D1%8F+iOS+%D0%B8+Android+%23habr\" title=\"Опубликовать ссылку в Twitter\" target=\"_blank\"></a> 		</div> 		<div class=\"vkontakte\"> 			<a href=\"http://vkontakte.ru/share.php?url=http://habr.ru/p/145155/\" title=\"Опубликовать ссылку во ВКонтакте\" onclick=\"return true;\"></a> 		</div> 		<div class=\"facebook\"> 			<a href=\"https://www.facebook.com/sharer/sharer.php?u=http://habr.ru/p/145155/\" title=\"Опубликовать ссылку в Facebook\" onclick=\"return true;\"></a> 		</div> 		<div class=\"googleplus\"> 			<a href=\"https://plus.google.com/share?url=http://habr.ru/p/145155/\" title=\"Опубликовать ссылку в Google Plus\" onclick=\"return true;\"></a> 		</div> 		 	  	  	  	 	  	 	 	<div class=\"author\">  		<a title=\"Автор текста\" href=\"http://habrahabr.ru/users/HugC/\" >HugC</a> 		<span class=\"rating\" title=\"рейтинг пользователя\">374,8</span> 		 	</div> 	 	  	  	  </div> <div class=\"clear\"></div>         <script type=\"text/javascript\" src=\"http://habrahabr.ru/core/viewcount/145155/\"></script> 	\";}', 0x313333383833303336352E34313136, null, null, '2012-06-04 21:19:25', '7');
INSERT INTO `tbl_content` VALUES ('8', 'a:3:{s:6:\"header\";s:0:\"\";s:5:\"autor\";s:0:\"\";s:7:\"content\";s:6275:\"     <h1 class=\"title\">        	      	                      <span class=\"post_title\">Арифмометр Brunsviga Nova, 1935 года — обзор и демонстрация работы механического калькулятора</span>                      			          	</h1>   	   	 <div class=\"hubs\"> 	 		<a href=\"http://habrahabr.ru/hub/atnikvariat/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Старое железо</a>,  	 		<a href=\"http://habrahabr.ru/hub/hardware/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Железо</a>,  	 		<a href=\"http://habrahabr.ru/company/gtv/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Блог компании GTV — видеоканал про технику</a> 	 </div>    	   	<div class=\"content html_format\">   	<a href=\"http://habrahabr.ru/company/gtv/blog/145168/\"><img src=\"/uploads/f04d47d0496ba442ff895d379447feff.jpg\" alt=\"image\"/></a><br/>  <br/>  В продолжении &quot;<i>Технохроники</i>&quot; на канале <a href=\"http://goldphone.tv\">GTV</a> предлагаем посмотреть сюжет с обзором и демонстрацией работы механического калькулятора — <b>Brunsviga Nova ZG 13</b>. Этот немецкий арифмометр поступил в производство в 1935 году и был предназначен для облегчения процесса бухгалтерского учета.<br/>  <br/>  <img src=\"/uploads/1a1afe342e8a9162e7c6897eb1779122.jpg\" alt=\"image\"/> <img src=\"/uploads/6cf02146bc9098b09c6f14196ea29ac6.jpg\" alt=\"image\"/><br/>  <br/>  <img src=\"/uploads/32f36b120076ca8545d6e05fdf69ab59.jpg\" alt=\"image\"/> <img src=\"/uploads/ffbd9f8759b19988d1628d3b7a45336c.jpg\" alt=\"image\"/><br/>  <br/>  Видео под катом.<br/>  <a name=\"habracut\"></a><br/>  <iframe width=\"560\" height=\"349\" src=\"http://www.youtube.com/embed/wHTzIJG09ck\" frameborder=\"0\" allowfullscreen></iframe>    	   	<div class=\"clear\"></div> 	   	</div>      <ul class=\"tags\"> 	<li ><a href=\"http://habrahabr.ru/search/?q=%5B%D0%B0%D1%80%D0%B8%D1%84%D0%BC%D0%BE%D0%BC%D0%B5%D1%82%D1%80%5D&target_type=posts\" rel=\"tag\">арифмометр</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5B%D0%BA%D0%B0%D0%BB%D1%8C%D0%BA%D1%83%D0%BB%D1%8F%D1%82%D0%BE%D1%80%5D&target_type=posts\" rel=\"tag\">калькулятор</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5B%D0%B0%D0%BD%D0%B0%D0%BB%D0%BE%D0%B3%D0%BE%D0%B2%D1%8B%D0%B9%20%D0%BA%D0%BE%D0%BC%D0%BF%D1%8C%D1%8E%D1%82%D0%B5%D1%80%5D&target_type=posts\" rel=\"tag\">аналоговый компьютер</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5B%D0%BE%D0%B1%D0%B7%D0%BE%D1%80%5D&target_type=posts\" rel=\"tag\">обзор</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5B%D0%B2%D0%B8%D0%B4%D0%B5%D0%BE%D0%BE%D0%B1%D0%B7%D0%BE%D1%80%5D&target_type=posts\" rel=\"tag\">видеообзор</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5B%D0%B4%D0%B5%D0%BC%D0%BE%D0%BD%D1%81%D1%82%D1%80%D0%B0%D1%86%D0%B8%D1%8F%20%D1%80%D0%B0%D0%B1%D0%BE%D1%82%D1%8B%5D&target_type=posts\" rel=\"tag\">демонстрация работы</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5Bgtv%5D&target_type=posts\" rel=\"tag\">gtv</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5Bgoldphone%5D&target_type=posts\" rel=\"tag\">goldphone</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5BBrunsviga%5D&target_type=posts\" rel=\"tag\">Brunsviga</a></li> </ul>      <div class=\"infopanel\" id=\"infopanel_post_145168\"> 	          	 	<div class=\"voting\">  		 		 		<span class=\"plus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		<span class=\"minus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		  		<div class=\"mark\"> 			 			 			 			 			<span class=\"score\" title=\"Результат голосования виден только авторизованным пользователям\">&#8212;</span> 			 			 		</div>  	</div> 	 	    	<div class=\"published\"> 4 июня 2012, 16:04</div>  	<div class=\"favorite\"> 		 		 		<a class=\"guest\"  href=\"#\" onclick=\"return true;\" title=\"Только зарегистрированные пользователи могут добавлять посты в избранное\"></a> 		 	</div>  	<div class=\"favs_count\" title=\"Количество пользователей, добавивших пост в избранное\">33</div>  	 		 		 		<div class=\"twitter\"> 			<a href=\"http://twitter.com/intent/tweet?text=http://habr.ru/p/145168/+%D0%90%D1%80%D0%B8%D1%84%D0%BC%D0%BE%D0%BC%D0%B5%D1%82%D1%80+Brunsviga+Nova%2C+1935+%D0%B3%D0%BE%D0%B4%D0%B0+%E2%80%94+%D0%BE%D0%B1%D0%B7%D0%BE%D1%80+%D0%B8+%D0%B4%D0%B5%D0%BC%D0%BE%D0%BD%D1%81%D1%82%D1%80%D0%B0%D1%86%D0%B8%D1%8F+%D1%80%D0%B0%D0%B1%D0%BE%D1%82%D1%8B+%D0%BC%D0%B5%D1%85%D0%B0%D0%BD%D0%B8%D1%87%D0%B5%D1%81%D0%BA%D0%BE%D0%B3%D0%BE+%D0%BA%D0%B0%D0%BB%D1%8C%D0%BA%D1%83%D0%BB%D1%8F%D1%82%D0%BE%D1%80%D0%B0+%23habr\" title=\"Опубликовать ссылку в Twitter\" target=\"_blank\"></a> 		</div> 		<div class=\"vkontakte\"> 			<a href=\"http://vkontakte.ru/share.php?url=http://habr.ru/p/145168/\" title=\"Опубликовать ссылку во ВКонтакте\" onclick=\"return true;\"></a> 		</div> 		<div class=\"facebook\"> 			<a href=\"https://www.facebook.com/sharer/sharer.php?u=http://habr.ru/p/145168/\" title=\"Опубликовать ссылку в Facebook\" onclick=\"return true;\"></a> 		</div> 		<div class=\"googleplus\"> 			<a href=\"https://plus.google.com/share?url=http://habr.ru/p/145168/\" title=\"Опубликовать ссылку в Google Plus\" onclick=\"return true;\"></a> 		</div> 		 	  	  	  	 	  	 	 	<div class=\"author\">  		<a title=\"Автор текста\" href=\"http://habrahabr.ru/users/Bredun/\" >Bredun</a> 		<span class=\"rating\" title=\"рейтинг пользователя\">209,7</span> 		 	</div> 	 	  	  	  </div> <div class=\"clear\"></div>         <script type=\"text/javascript\" src=\"http://habrahabr.ru/core/viewcount/145168/\"></script> 	\";}', 0x313333383833303336382E36333534, null, null, '2012-06-04 21:19:28', '7');
INSERT INTO `tbl_content` VALUES ('9', 'a:3:{s:6:\"header\";s:0:\"\";s:5:\"autor\";s:0:\"\";s:7:\"content\";s:6068:\"     <h1 class=\"title\">        	      	                      <span class=\"post_title\">The Pirate Bay заблокирован в Нидерландах</span>                      			          	</h1>   	   	 <div class=\"hubs\"> 	 		<a href=\"http://habrahabr.ru/hub/Dura_Lex/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Dura Lex</a> 	 </div>    	   	<div class=\"content html_format\">   	Это таки произошло. С недавнего времени оператор T-Mobile вместо привычной уже картинки с пиратским флагом отдает вот такую вот страничку.<br/>  <br/>  <img src=\"/uploads/d85cd074cee6de296ab2e3a8faeaf025.png\"/><br/>  <br/>  На странице блокировки написано, что решение <a href=\"http://habrahabr.ru/post/143612/\">было принято</a> 10 мая районным судом Гааги во время слушания дела Stichting Brein против T-Mobile Netherlands B.V.<br/>  <br/>  Суд рекомендует провайдерам в течение десяти дней после оглашения решения прекратить доступ к доменному имени и айпи-адресам Пиратской бухты.<br/>  <a name=\"habracut\"></a><br/>  С полным текстом постановления можно ознакомиться <a href=\"http://zoeken.rechtspraak.nl/detailpage.aspx?ljn=BW5387\">здесь</a> (ахтунг, всё по-голландски).<br/>  <br/>  Ответчиками по делу кроме T-Mobile выступали также операторы UPC, KPN и TELE2. Компания Stichting Brein, выступавшая истцом — это местная ассоциация <s>поганых копирастов</s> невозбранных любителей охраны авторских прав, которая пыталась закрыть доступ к The Pirate Bay еще с 2009 года. До последнего времени провайдеры как-то отбивались, но были вынуждены уступить.<br/>  <br/>  Напомню, что тучи над бухтой сгущаются не только в Нидерландах. Так, решение о запрете доступа к сайту недавно <a href=\"http://habrahabr.ru/post/143215/\">было принято</a> в Великобритании. Нидерланды же недавно <a href=\"http://habrahabr.ru/post/143558/\">приняли закон о сетевом нейтралитете</a>, согласно которому принудительное ограничение доступа к определенным сайтам возможно не иначе как по решению суда. Но The Pirate Bay это, как видим, не помогло.<br/>  <br/>  P.S. А забавно всё-таки смотрится слоган T-Mobile.    	   	<div class=\"clear\"></div> 	   	</div>      <ul class=\"tags\"> 	<li ><a href=\"http://habrahabr.ru/search/?q=%5Bthe%20pirate%20bay%5D&target_type=posts\" rel=\"tag\">the pirate bay</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5B%D0%BA%D0%BE%D0%BF%D0%B8%D1%80%D0%B0%D1%81%D1%82%D1%8B%5D&target_type=posts\" rel=\"tag\">копирасты</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5B%D0%B4%D0%BE%D0%B1%D1%80%D0%BE%20%D0%B2%D1%81%D0%B5%20%D1%80%D0%B0%D0%B2%D0%BD%D0%BE%20%D0%B2%D0%BE%D1%81%D1%82%D0%BE%D1%80%D0%B6%D0%B5%D1%81%D1%82%D0%B2%D1%83%D0%B5%D1%82%5D&target_type=posts\" rel=\"tag\">добро все равно восторжествует</a></li> </ul>      <div class=\"infopanel\" id=\"infopanel_post_145118\"> 	          	 	<div class=\"voting\">  		 		 		<span class=\"plus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		<span class=\"minus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		  		<div class=\"mark\"> 			 			 			 			 			<span class=\"score\" title=\"Результат голосования виден только авторизованным пользователям\">&#8212;</span> 			 			 		</div>  	</div> 	 	    	<div class=\"published\"> 4 июня 2012, 02:19</div>  	<div class=\"favorite\"> 		 		 		<a class=\"guest\"  href=\"#\" onclick=\"return true;\" title=\"Только зарегистрированные пользователи могут добавлять посты в избранное\"></a> 		 	</div>  	<div class=\"favs_count\" title=\"Количество пользователей, добавивших пост в избранное\">7</div>  	 		 		 		<div class=\"twitter\"> 			<a href=\"http://twitter.com/intent/tweet?text=http://habr.ru/p/145118/+The+Pirate+Bay+%D0%B7%D0%B0%D0%B1%D0%BB%D0%BE%D0%BA%D0%B8%D1%80%D0%BE%D0%B2%D0%B0%D0%BD+%D0%B2+%D0%9D%D0%B8%D0%B4%D0%B5%D1%80%D0%BB%D0%B0%D0%BD%D0%B4%D0%B0%D1%85+%23habr\" title=\"Опубликовать ссылку в Twitter\" target=\"_blank\"></a> 		</div> 		<div class=\"vkontakte\"> 			<a href=\"http://vkontakte.ru/share.php?url=http://habr.ru/p/145118/\" title=\"Опубликовать ссылку во ВКонтакте\" onclick=\"return true;\"></a> 		</div> 		<div class=\"facebook\"> 			<a href=\"https://www.facebook.com/sharer/sharer.php?u=http://habr.ru/p/145118/\" title=\"Опубликовать ссылку в Facebook\" onclick=\"return true;\"></a> 		</div> 		<div class=\"googleplus\"> 			<a href=\"https://plus.google.com/share?url=http://habr.ru/p/145118/\" title=\"Опубликовать ссылку в Google Plus\" onclick=\"return true;\"></a> 		</div> 		 	  	  	  	 	  	 	 	<div class=\"author\">  		<a title=\"Автор текста\" href=\"http://habrahabr.ru/users/dzzh/\" >dzzh</a> 		<span class=\"rating\" title=\"рейтинг пользователя\">30,3</span> 		 	</div> 	 	  	  	  </div> <div class=\"clear\"></div>          	\";}', 0x313333383833303337312E34383933, null, null, '2012-06-04 21:19:31', '7');
INSERT INTO `tbl_content` VALUES ('10', 'a:3:{s:6:\"header\";s:0:\"\";s:5:\"autor\";s:0:\"\";s:7:\"content\";s:7290:\"     <h1 class=\"title\">        	      	                      <span class=\"post_title\">Простой метод хранения паролей в голове</span>                      			<a href=\"/info/help/karma/#recovery\" class=\"recovery_mode\" title=\"Реабилитационный пост от пользователя с отрицательной кармой\">recovery mode</a>          	</h1>   	   	 <div class=\"hubs\"> 	 		<a href=\"http://habrahabr.ru/hub/infosecurity/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Информационная безопасность</a><span class=\"profiled_hub\" title=\"Профильный хаб\">*</span> 	 </div>    	   	<div class=\"content html_format\">   	<img src=\"/uploads/40c6911ac2c83799729325ea5db9b4a4.jpg\"/><br/>  <br/>  Я регистрируюсь в среднем, на одном сайте в неделю и мне, как и остальным, необходимо постоянно придумывать и главное помнить свои пароли. Сначала я придумал сложный пароль и забивал его на всех доменах где регистрировался, но потом понял, что это не далеко не безопасный подход, так как при взломе одного аккаунта, ты рискуешь потерять все остальные. Поэтому было принято решение хранить разные пароли для разных доменов.<br/>  <a name=\"habracut\"></a><br/>  <br/>  Один из первых инструментов которым я пользовался — Truecrypt. Удобная и бесплатная штука, но к сожалению хранить там пароли неудобно, так как я захожу на различные сервисы и из дома, и с работы и с планшета.<br/>  <br/>  И, вообще, хочется отметить, что записывать пароли куда-либо небезопасно. Существует множество сервисов и программ по хранению паролей, но в любом случае это промежуточное звено, к тому же часть из них платные.<br/>  <br/>  Я хотел бы рассказать о системе хранения паролей, к которой я пришел. <br/>  <br/>  Все храниться в голове, необходимо лишь придумать и запомнить (можно для верности и записать) одну основную маску пароля и применять ее к различным сервисам.<br/>  <br/>  Например, домен twitter.com, пароль: 7rxm!0tW<br/>  <br/>  где:<br/>  7 — количество букв в домене<br/>  rxm!0 — постоянный пароль<br/>  tW — первые две буквы домена, причем вторая в верхнем регистре<br/>  <br/>  Таким образом:<br/>  <br/>  gmail.com — 5rxm!0gM<br/>  habrahabr.ru — 9rxm!0hA<br/>  <br/>  Как вы видите, пароли получаются довольно качественные и их совсем не надо помнить, вы сможете воспроизвести пароль для любого домена за несколько секунд, помня лишь маску к нему. Со временем, все доводиться до автоматизма и вы просто вбиваете любой пароль к любому сайту даже не задумываясь.<br/>  <br/>  Естественно маску необходимо не повторять, а придумать свою! Можно учитывать число гласных и согласных, заменять цифры их аналогами в верхнем регистре и так далее.<br/>  <br/>  Буду рад если кому-то данный метод хранения паролей окажется полезным, а также если кто-то укажет на недостатки данной системы хранения паролей.    	   	<div class=\"clear\"></div> 	   	</div>      <ul class=\"tags\"> 	<li ><a href=\"http://habrahabr.ru/search/?q=%5B%D0%BF%D0%B0%D1%80%D0%BE%D0%BB%D0%B8%5D&target_type=posts\" rel=\"tag\">пароли</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5B%D0%B1%D0%B5%D0%B7%D0%BE%D0%BF%D0%B0%D1%81%D0%BD%D0%BE%D1%81%D1%82%D1%8C%5D&target_type=posts\" rel=\"tag\">безопасность</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5Bweb%5D&target_type=posts\" rel=\"tag\">web</a></li> </ul>      <div class=\"infopanel\" id=\"infopanel_post_145166\"> 	          	 	<div class=\"voting\">  		 		 		<span class=\"plus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		<span class=\"minus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		  		<div class=\"mark\"> 			 			 			 			 			<span class=\"score\" title=\"Результат голосования виден только авторизованным пользователям\">&#8212;</span> 			 			 		</div>  	</div> 	 	    	<div class=\"published\"> 4 июня 2012, 15:39</div>  	<div class=\"favorite\"> 		 		 		<a class=\"guest\"  href=\"#\" onclick=\"return true;\" title=\"Только зарегистрированные пользователи могут добавлять посты в избранное\"></a> 		 	</div>  	<div class=\"favs_count\" title=\"Количество пользователей, добавивших пост в избранное\">105</div>  	 		 		 		<div class=\"twitter\"> 			<a href=\"http://twitter.com/intent/tweet?text=http://habr.ru/p/145166/+%D0%9F%D1%80%D0%BE%D1%81%D1%82%D0%BE%D0%B9+%D0%BC%D0%B5%D1%82%D0%BE%D0%B4+%D1%85%D1%80%D0%B0%D0%BD%D0%B5%D0%BD%D0%B8%D1%8F+%D0%BF%D0%B0%D1%80%D0%BE%D0%BB%D0%B5%D0%B9+%D0%B2+%D0%B3%D0%BE%D0%BB%D0%BE%D0%B2%D0%B5+%23habr\" title=\"Опубликовать ссылку в Twitter\" target=\"_blank\"></a> 		</div> 		<div class=\"vkontakte\"> 			<a href=\"http://vkontakte.ru/share.php?url=http://habr.ru/p/145166/\" title=\"Опубликовать ссылку во ВКонтакте\" onclick=\"return true;\"></a> 		</div> 		<div class=\"facebook\"> 			<a href=\"https://www.facebook.com/sharer/sharer.php?u=http://habr.ru/p/145166/\" title=\"Опубликовать ссылку в Facebook\" onclick=\"return true;\"></a> 		</div> 		<div class=\"googleplus\"> 			<a href=\"https://plus.google.com/share?url=http://habr.ru/p/145166/\" title=\"Опубликовать ссылку в Google Plus\" onclick=\"return true;\"></a> 		</div> 		 	  	  	  	 	  	 	 	<div class=\"author\">  		<a title=\"Автор текста\" href=\"http://habrahabr.ru/users/MarkTrofimoff/\" >MarkTrofimoff</a> 		<span class=\"rating\" title=\"рейтинг пользователя\">52,8</span> 		 	</div> 	 	  	  	  </div> <div class=\"clear\"></div>          	\";}', 0x313333383833303337352E30343635, null, null, '2012-06-04 21:19:35', '7');
INSERT INTO `tbl_content` VALUES ('11', 'a:3:{s:6:\"header\";s:0:\"\";s:5:\"autor\";s:0:\"\";s:7:\"content\";s:22544:\"     <h1 class=\"title\">        	      	                      <span class=\"post_title\">Обзор DDoS-атак Рунета: 9:1 в пользу примитива</span>                      			          	</h1>   	   	 <div class=\"hubs\"> 	 		<a href=\"http://habrahabr.ru/hub/sys_admin/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Системное администрирование</a><span class=\"profiled_hub\" title=\"Профильный хаб\">*</span>,  	 		<a href=\"http://habrahabr.ru/hub/infosecurity/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Информационная безопасность</a><span class=\"profiled_hub\" title=\"Профильный хаб\">*</span>,  	 		<a href=\"http://habrahabr.ru/company/highloadlab/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Блог компании Highload Lab.</a> 	 </div>    	   	<div class=\"content html_format\">   	Отчетная статья <a href=\"http://rigf.ru/prog/?p=section3\">по семинару «DDoS-атаки и защита от них»</a> (RIGF, 14 мая 2012, Москва)<br/>  <br/>  <b>Спикер:</b> <a href=\"http://habrahabr.ru/users/ximaera/\">Артем Гавриченков</a>, ведущий разработчик сети фильтрации трафика Qrator<br/>  <b>Тема доклада:</b> DDoS-атаки в 2011-2012 гг.: характер и тенденции<br/>  <br/>  Приведем обзор DDoS-атак за последние полтора года и поговорим о том, чего следует бояться, а чего нет. Начнем с тривиальной статистики, данные которой основаны на клиентской базе HLL: к сожалению, ни одна компания не имеет универсального всевидящего ока. Итак, за 2011-2012 гг. мы стали очевидцами более 2 500 DDoS-атак.<br/>  <br/>  <img src=\"/uploads/b6e208b01600b8cf80e9434cd9cd8223.jpg\"/><br/>  <br/>  <a name=\"habracut\"></a><h4><s>No</s> Time for losers</h4><br/>  <b>ICMP/UDP/SYN/ACK-флуд составляет всего 18%.</b> Средняя мощность атаки – 300 Мбит/c. Более 1 Гбит/c имели 40 инцидентов. Наибольший суммарный трафик атаки на полосу – 56 Гбит/c – зафиксирован на интернет-магазин магнитных игрушек в июле 2011 года. Атака была частично направлена и на наш домен, есть основания полагать, что атакующие пытались обрушить Qrator целиком, «размазав» трафик атаки по сети.<br/>  <br/>  Количество DDoS-атак на полосу постоянно снижается. А все потому, что большинство хостинг-провайдеров среднего уровня стали к ним готовы. Небольшим атакам легко противодействовать, мощные же не могут продолжаться долго, так как даже магистральные провайдеры начинают испытывать связанные с ними проблемы.<br/>  <br/>  <h4>The Champions!</h4><br/>  Преступники отдают <b>предпочтение высокоуровневым атакам транспортного и прикладного уровня (82%).</b> Они рассчитывают на исчерпание ресурсов сервера в рамках установленных TCP-соединений, поэтому важно количество IP-адресов, участвующих в атаке. Интеллектуальные атаки на приложение коварны тем, что даже при наличии стратегии защиты вы скорее всего потеряете некую, пусть мизерную, часть легитимных пользователей.<br/>  <br/>  Не менее 50% атак Layer 7 – это «долбежка» в один URI, <b>интеллектуальных атак всего около 10%</b>. Первый массовый робот, который имел хорошо написанный сетевой стек с HTTP и Javascript и грамотно осуществлял переходы по сайту, – это <a href=\"http://about-threats.trendmicro.com/Malware.aspx?language=us&amp;name=BKDR_BTMINE.DDOS\">Minerbot</a>, атаковавший прошлой осенью немецкие и российские сайты e-commerce. Количество ботов с полноценными Web browser capabilities (TCP, HTTP/1.1, JS) неуклонно растет, особенно в дорогих заказных атаках. Вероятно, индустрия движется в сторону использования полноценных браузеров для выполнения DDoS-атак.<br/>  <br/>  <img src=\"/uploads/5de4dbc259ceb658b56f3a7f1bf78284.jpg\"/><br/>  <br/>  Отметим еще одну пренеприятнейшую тенденцию – владельцы ботнетов стали кооперироваться. За год Qrator детектировал 4 атаки, осуществляемые одновременно несколькими ботнетами с различными стратегиями нападения. Вспомним громкие случаи с <a href=\"http://habrahabr.ru/company/highloadlab/blog/143404/\">«Эхо Москвы»</a> (3 ботнета) и <a href=\"http://habrahabr.ru/company/highloadlab/blog/125018/\">Хабрахабром</a> (4-5).<br/>  <br/>  <h4>Keep on fighting – till the end</h4><br/>  С января по декабрь 2011 года максимальная продолжительность атаки составила <b>486 часов.</b> Как это было? Ботнет из стран СНГ и Балтии атаковал интернет-магазин кедровых бочек. Первые трое суток он пытался пробить защиту, но разочаровавшись, пошел зарабатывать деньги где-то еще. Зато как только мы начинали разблокировку IP-адресов, он тут же возвращался. Атакующий держал двух-трех ботов, и как только запрос проходил, то вызывал еще 5 000. В таком режиме ботнет может функционировать вечно, осуществляя атаки на несколько сайтов при минимальных ресурсах.<br/>  <br/>  <h4>When I get older I will be stronger</h4><br/>  <img src=\"/uploads/da54e120cf7fb5e52f6cc44f19ce7d92.jpg\" align=\"left\"/>4 декабря 2011 года выяснилось, что о ресурсах ботнета заботятся не все. Максимальная продолжительность DDoS-атаки достигла <b>1228 часов.</b> Если бы <a href=\"http://slon.ru/\">slon.ru/</a> не находился под защитой, то он бы лежал 2 месяца. В течение пары часов на сайт зашло более 200 тысяч ботов преимущественно из стран Южной и Юго-Восточной Азии. Таким образом, атака поставила еще один рекорд в истории Qrator – по размеру ботнета. И хотя она уже не приносила никакого результата, атакующий все равно держал трафик, причем ботов было очень много. Но самое интересное, что, по всей видимости, этот же самый ботнет размером порядка 182 000 машин вернулся на <a href=\"http://slon.ru/\">slon.ru/</a> и на <a href=\"http://tvrain.ru/\">tvrain.ru/</a> 6 мая 2012 года, причём пересечение «декабрьского» и «майского» стоп-листов составило около 500 IP-адресов. <b>За полгода ботнет из пары сотен тысяч машин обновился почти полностью – либо же на самом деле он в разы больше.</b><br/>  <br/>  Кстати, при атаках 4 декабря 2011 года мы ожидали, что ботнеты будут пересекаться: ведь громили схожие по идеологии СМИ. Однако пересечения замечено не было. Зато в мае мы четко видели, как один ботнет работал и против российских, и против армянских СМИ.<br/>  <br/>  <h4>Give me freedom, give me fire</h4><br/>  Координация ботнетов растет. Все чаще мы имеем дело не с ботнетом, имеющим единый управляющий центр, который не может работать под большой нагрузкой, а с пиринговой сетью. Поэтому обнаружение источника атаки становится все более сложным, особенно при отсутствии зацепок нетехнического характера. <b>Организаторов DDoS-атаки поймать практически невозможно.</b><br/>  <br/>  Хотя не так давно мы нашли источник одной атаки на интернет-СМИ и передали его IP-адрес владельцу ресурса (который, в свою очередь, передал его правоохранительным органам). Злоумышленник сначала использовал ботнет, но в момент, когда сайт оказался под защитой, он привлек другие ресурсы, включая список анонимных прокси. Некоторые из них оказались не столь уж анонимными и проставляли HTTP-заголовок X-Forwarded-For, в котором, собственно, и находился IP-адрес атакующего сервера. Довольно тривиально, но выясняется, что не все об этом думают. Вероятность ошибки мала: у потенциального исполнителя были найдены личные мотивы.<br/>  <br/>  <h4>And if you fall, get up, eh eh</h4><br/>  <img src=\"/uploads/a23600e86e28deb85f52d7fba30664a2.jpg\" align=\"left\"/>Говоря о распределении DDoS-атак <b>по месяцам</b>, отметим, что с июня по сентябрь преступники снижают свою активность. <b>По дням недели</b> их усилия распределены практически равномерно с некоторым спадом на выходных. Зато, как правило, на выходных как раз и случаются наиболее мощные атаки на полосу. C чем это связано? Если, допустим, атака в 56 Гбит/c случается во вторник, то есть определенные основания надеяться, что в ближайшее время кто-нибудь из магистральщиков среагирует и мы сможем с ним договориться. В выходные же техподдержка даже крупных провайдеров часто <s>не делает ни х</s> работает менее качественно, чем в будни.<br/>  <br/>  <h4>But it\'s been no bed of roses</h4><br/>  Пока что Россию спасает ADSL: на вход большой, на выход маленький. Поэтому много атак на полосу не происходит. Но не забывайте <b>о такой страшилке, как работа IXP,</b> которые всячески игнорируют поступающие к ним abuse request\'ы. Как это выглядит? На одного из наших клиентов начинается крупная атака, которая создает проблемы некоторым площадкам, на которых мы расположены. Причем мы видим, что мусорный трафик идет из некоторого Internet Exchange. Чтобы лишний раз не балансировать сеть, мы вначале пишем им вежливый опус вроде «От одного из ваших клиентов идет ICMP-флуд приличного объема, это неправильно, разберитесь».<br/>  <br/>  <i>«Today we faced several Gbps of DoS from your exchange (and at this time it hasn\'t stopped yet).<br/>  It is ICMP traffic with spoofed source addresses.<br/>  Our suggestion is that one of your clients uses IPs from other ASes, connected to *^#$^*-IX.»</i><br/>  <br/>  Ответ мы получаем примерно такой: «Технически ничего поделать не можем. Вообще, мы предоставляем клиенту услугу, он платит нам деньги, может, вы его пустите в null route?»<br/>  <br/>  <i>«*^#$^*-IX only operates the L2 exchange fabric. We are not involved in routing issues ourselves. Please ask your upstream(s) to contact their relevant peers on the exchange in order to investigate this.<br/>  One idea is that you could add a null route at the border?»</i><br/>  <br/>  В null route мы-то его пустим, но тогда он придет к нам не через этот IX, а через «Ростелеком», например. Толку от этого никакого. Мы <a href=\"http://ximaera.livejournal.com/77003.html\">довольно долго говорим</a> о том, что <b>у провайдеров, IXP и ISP должна быть какая-то сознательность</b>, готовность работать с такими вещами. Но, к сожалению, пока мы ее не очень-то видим. <br/>  <br/>  <h4>Choosing your battles</h4><br/>  <b>Поговорим о целях атакующих.</b> К традиционным финансы-политика-социальный протест-кража данных-самореклама ботнетов добавилась новая цель – вымогательство биткойнов. Преступники отправляют письмо с требованием выдать определенную сумму в биткойнах, а в случае непослушания угрожают крупной DDoS-атакой. И, как правило, бездействие жертвы действительно сопровождается интеллектуальной атакой на приложение. Все уже, наверное, привыкли к <a href=\"http://ximaera.livejournal.com/80486.html\">письмам от школьников</a> из серии «Переведите мне 1000$ на Яндекс.Деньги, или ваш сайт будет лежать две недели». Это то же самое, но на качественно новом уровне, и этого уже следует опасаться. А поскольку биткойны не являются официальным платежным средством, то привлечь за вымогательство в данном случае практически невозможно.<br/>  <br/>  <h4>Every nations, all around us</h4><br/>  Напоследок мы хотим всем напомнить, что не нужно иметь атаку в 6-7 Гбит/c, чтобы сложиться. Опасна как раз таргетированная атака на приложение даже с небольшим ботнетом. До 2010 года борьба с DDoS-атаками для российских веб-приложений заключалась в блокировке зарубежного трафика. Сегодня любой желающий может приобрести ботов с геопривязкой по любым регионам. Да, российские ботнеты немногочисленны, но они есть.<br/>  <br/>  <h4>Achtung!</h4><br/>  Акцентируем внимание еще на одном важном пункте. Если на приложение пришла атака в 5000 ботов и оно «легло в спячку», то это не значит, что у атакующего не было в запасе дополнительных ботов. Как только ресурс не складывается под небольшой нагрузкой, как правило, 1-2 раза происходит наращивание мощности атаки, бывает и десятикратный прирост. Поэтому <b>готовьте сани летом.</b> И помните, что в случае DDoS-атаки вам придется заплатить за неработающий сайт. Если обычно вы платите провайдеру за 2-3 мегабита, то после инцидента будьте готовы к счету до пяти нулей. Согласитесь, будет обидно.<br/>  <br/>  <h4>Let\'s rejoice in the beautiful game</h4><br/>  90% DDoS-атак – это довольно примитивные, безынтересные игры. Численное превосходство «гостей» – не повод для паники. Спасение бегством – не лучшая стратегия. Тренируйтесь загодя, постройте плотную защиту, вооружитесь тактикой, импровизируйте. Играйте по своим правилам. В конце концов, игра идёт на вашем поле.<br/>  <br/>  <iframe width=\"560\" height=\"349\" src=\"http://www.youtube.com/embed/yqpU5q2zJyM\" frameborder=\"0\" allowfullscreen></iframe>    	   	<div class=\"clear\"></div> 	   	</div>      <ul class=\"tags\"> 	<li ><a href=\"http://habrahabr.ru/search/?q=%5Bddos-%D0%B0%D1%82%D0%B0%D0%BA%D0%B0%5D&target_type=posts\" rel=\"tag\">ddos-атака</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5B%D0%B7%D0%B0%D1%89%D0%B8%D1%82%D0%B0%20%D0%BE%D1%82%20ddos%5D&target_type=posts\" rel=\"tag\">защита от ddos</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5B%D0%B1%D0%BE%D1%82%D0%BD%D0%B5%D1%82%5D&target_type=posts\" rel=\"tag\">ботнет</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5Bhll%5D&target_type=posts\" rel=\"tag\">hll</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5Bqrator%5D&target_type=posts\" rel=\"tag\">qrator</a></li> </ul>      <div class=\"infopanel\" id=\"infopanel_post_145137\"> 	          	 	<div class=\"voting\">  		 		 		<span class=\"plus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		<span class=\"minus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		  		<div class=\"mark\"> 			 			 			 			 			<span class=\"score\" title=\"Результат голосования виден только авторизованным пользователям\">&#8212;</span> 			 			 		</div>  	</div> 	 	    	<div class=\"published\"> 4 июня 2012, 12:03</div>  	<div class=\"favorite\"> 		 		 		<a class=\"guest\"  href=\"#\" onclick=\"return true;\" title=\"Только зарегистрированные пользователи могут добавлять посты в избранное\"></a> 		 	</div>  	<div class=\"favs_count\" title=\"Количество пользователей, добавивших пост в избранное\">73</div>  	 		 		 		<div class=\"twitter\"> 			<a href=\"http://twitter.com/intent/tweet?text=http://habr.ru/p/145137/+%D0%9E%D0%B1%D0%B7%D0%BE%D1%80+DDoS-%D0%B0%D1%82%D0%B0%D0%BA+%D0%A0%D1%83%D0%BD%D0%B5%D1%82%D0%B0%3A+9%3A1+%D0%B2+%D0%BF%D0%BE%D0%BB%D1%8C%D0%B7%D1%83+%D0%BF%D1%80%D0%B8%D0%BC%D0%B8%D1%82%D0%B8%D0%B2%D0%B0+%23habr\" title=\"Опубликовать ссылку в Twitter\" target=\"_blank\"></a> 		</div> 		<div class=\"vkontakte\"> 			<a href=\"http://vkontakte.ru/share.php?url=http://habr.ru/p/145137/\" title=\"Опубликовать ссылку во ВКонтакте\" onclick=\"return true;\"></a> 		</div> 		<div class=\"facebook\"> 			<a href=\"https://www.facebook.com/sharer/sharer.php?u=http://habr.ru/p/145137/\" title=\"Опубликовать ссылку в Facebook\" onclick=\"return true;\"></a> 		</div> 		<div class=\"googleplus\"> 			<a href=\"https://plus.google.com/share?url=http://habr.ru/p/145137/\" title=\"Опубликовать ссылку в Google Plus\" onclick=\"return true;\"></a> 		</div> 		 	  	  	  	 	  	 	 	<div class=\"author\">  		<a title=\"Автор текста\" href=\"http://habrahabr.ru/users/ximaera/\" >ximaera</a> 		<span class=\"rating\" title=\"рейтинг пользователя\">68,8</span> 		 			<a href=\"https://plus.google.com/114478240785234202546/?rel=author\" class=\"googleplus_profile\">G+</a> 		 	</div> 	 	  	  	  </div> <div class=\"clear\"></div>         <script type=\"text/javascript\" src=\"http://habrahabr.ru/core/viewcount/145137/\"></script> 	\";}', 0x313333383833303338362E30373733, null, null, '2012-06-04 21:19:46', '7');
INSERT INTO `tbl_content` VALUES ('12', 'a:3:{s:6:\"header\";s:0:\"\";s:5:\"autor\";s:0:\"\";s:7:\"content\";s:6023:\"     <h1 class=\"title\">        	      	                      <span class=\"post_title\">$25 на счет Amazon Web Services</span>                      			<a href=\"/info/help/karma/#recovery\" class=\"recovery_mode\" title=\"Реабилитационный пост от пользователя с отрицательной кармой\">recovery mode</a>          	</h1>   	   	 <div class=\"hubs\"> 	 		<a href=\"http://habrahabr.ru/hub/aws/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Amazon Web Services</a><span class=\"profiled_hub\" title=\"Профильный хаб\">*</span> 	 </div>    	   	<div class=\"content html_format\">   	<img src=\"/uploads/81c4baa504dc64c6a1cf48b37338ec2f.gif\"/><br/>  Уважаемые Хабрачитатели!<br/>  <br/>  Сегодняшний пост мой будет краток. <br/>  Я совершенно случайно обнаружил доселе неизведанный способ поднимать баланс AWS акка на $25.<br/>  Несмотря на возможность испортить малину все же поделюсь с Хабрасообществом<br/>  <a name=\"habracut\"></a><br/>  0. Регистрируем и активируем акк на AWS.<br/>  1. Регистрируемся на <a href=\"https://bigdatauniversity.com/courses/auth/openid/login.php\">bigdatauniversity.com/courses/auth/openid/login.php</a><br/>  Можно зарегиться несколько раз, например гугловым и фейсбучным акком отдельно.<br/>  2. <a href=\"http://bigdatauniversity.com/courses/enrol/index.php?id=309\">bigdatauniversity.com/courses/enrol/index.php?id=309</a><br/>  Жмем кнопочку enrol<br/>  3. <a href=\"http://bigdatauniversity.com/web/aws-credit.php?id=309\">bigdatauniversity.com/web/aws-credit.php?id=309</a><br/>  Жмем получить купон и ждем 25 долларов на указанную в формочке почту. <br/>  4. <a href=\"https://aws-portal.amazon.com/gp/aws/developer/account/index.html?ie=UTF8&action=credits\">aws-portal.amazon.com/gp/aws/developer/account/index.html?ie=UTF8&action=credits</a><br/>  А здесь получаем законно полученные доллары, введя код купона и нажав Redeem.<br/>  <br/>  P.S. За раз можно воспользоваться только одним купоном. Я не уверен, но надеюсь, что можно будет воспользоваться другими купонами, когда этот закончится.<br/>  Я активировал купон на одном акке, на нем висит один сервис <a href=\"http://addagio.ru\">addagio.ru</a><br/>  Если получится быстро слить деньги, оттестирую, работает ли активация несколько купонов подряд.<br/>  <br/>  <b>Надеюсь, у всех все получится. Удачи!!!</b><br/>  <br/>  P.P.S. Напишите в ЛС или в комменты, интересно ли почитать про высоконагруженные MySQL+Sphinx проекты, а также скоростные ограничения Амазона и как их обходить.    	   	<div class=\"clear\"></div> 	   	</div>      <ul class=\"tags\"> 	<li ><a href=\"http://habrahabr.ru/search/?q=%5Baws%5D&target_type=posts\" rel=\"tag\">aws</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5B%D1%85%D0%B0%D0%BB%D1%8F%D0%B2%D0%B0%5D&target_type=posts\" rel=\"tag\">халява</a></li> </ul>      <div class=\"infopanel\" id=\"infopanel_post_145119\"> 	          	 	<div class=\"voting\">  		 		 		<span class=\"plus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		<span class=\"minus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		  		<div class=\"mark\"> 			 			 			 			 			<span class=\"score\" title=\"Результат голосования виден только авторизованным пользователям\">&#8212;</span> 			 			 		</div>  	</div> 	 	    	<div class=\"published\"> 4 июня 2012, 03:00</div>  	<div class=\"favorite\"> 		 		 		<a class=\"guest\"  href=\"#\" onclick=\"return true;\" title=\"Только зарегистрированные пользователи могут добавлять посты в избранное\"></a> 		 	</div>  	<div class=\"favs_count\" title=\"Количество пользователей, добавивших пост в избранное\">90</div>  	 		 		 		<div class=\"twitter\"> 			<a href=\"http://twitter.com/intent/tweet?text=http://habr.ru/p/145119/+%2425+%D0%BD%D0%B0+%D1%81%D1%87%D0%B5%D1%82+Amazon+Web+Services+%23habr\" title=\"Опубликовать ссылку в Twitter\" target=\"_blank\"></a> 		</div> 		<div class=\"vkontakte\"> 			<a href=\"http://vkontakte.ru/share.php?url=http://habr.ru/p/145119/\" title=\"Опубликовать ссылку во ВКонтакте\" onclick=\"return true;\"></a> 		</div> 		<div class=\"facebook\"> 			<a href=\"https://www.facebook.com/sharer/sharer.php?u=http://habr.ru/p/145119/\" title=\"Опубликовать ссылку в Facebook\" onclick=\"return true;\"></a> 		</div> 		<div class=\"googleplus\"> 			<a href=\"https://plus.google.com/share?url=http://habr.ru/p/145119/\" title=\"Опубликовать ссылку в Google Plus\" onclick=\"return true;\"></a> 		</div> 		 	  	  	  	 	  	 	 	<div class=\"author\">  		<a title=\"Автор текста\" href=\"http://habrahabr.ru/users/Iliapan/\" >Iliapan</a> 		<span class=\"rating\" title=\"рейтинг пользователя\">38,0</span> 		 			<a href=\"https://plus.google.com/111224546073549495140/?rel=author\" class=\"googleplus_profile\">G+</a> 		 	</div> 	 	  	  	  </div> <div class=\"clear\"></div>          	\";}', 0x313333383833303338382E36383034, null, null, '2012-06-04 21:19:48', '7');
INSERT INTO `tbl_content` VALUES ('13', 'a:3:{s:6:\"header\";s:0:\"\";s:5:\"autor\";s:0:\"\";s:7:\"content\";s:6755:\"     <h1 class=\"title\">        	      	                      <span class=\"post_title\">Сергей Брин показал Google Glass в ТВ-интервью</span>                      			          	</h1>   	   	 <div class=\"hubs\"> 	 		<a href=\"http://habrahabr.ru/hub/ui/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Интерфейсы</a><span class=\"profiled_hub\" title=\"Профильный хаб\">*</span>,  	 		<a href=\"http://habrahabr.ru/hub/augmented_reality/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Дополненная реальность</a>,  	 		<a href=\"http://habrahabr.ru/hub/google/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Google</a> 	 </div>    	   	<div class=\"content html_format\">   	Ранее в интернете уже распространялись рекламные ролики, в которых демонстрировались возможности нового проекта Google — очков дополненной реальности Google Glass. <br/>  <br/>  Появившееся недавно короткое видео, в котором было показан вид «от первого лица» владельца Google Glass, больше похоже на то, что снимает камера видеорегистратора — самих очков, либо какого-либо интерфейса, либо элементов дополненной реальности показано не было.<br/>  <br/>  Это выглядело таким образом:<br/>  <div class=\"spoiler\"><b class=\"spoiler_title\">Скрытый текст</b><div class=\"spoiler_text\"><iframe width=\"560\" height=\"349\" src=\"http://www.youtube.com/embed/pZKx3akalYM\" frameborder=\"0\" allowfullscreen></iframe><br/>  </div></div> <br/>  <a name=\"habracut\"></a> <br/>  На прошлой неделе Сергей Брин показал в телеинтервью очки Google Glass «со стороны» — как вполне действующий прототип и даже дал надеть их ведущему, продемонстрировав таким образом серьёзный интерес своей компании к разработке. Брин на видео включает просмотр фото на очках при помощи некоей тач-панели, расположенной на правой стороне оправы, передаёт их ведущему, но почему-то просит его к ним не касаться; после ведущий выражает свой восторг. Также Брин выразил некоторую надежду, что очки удастся выпустить в следующем году.<br/>  <br/>  <iframe width=\"560\" height=\"349\" src=\"http://www.youtube.com/embed/8oPRJh3eCjA\" frameborder=\"0\" allowfullscreen></iframe><br/>  <br/>  Любопытно, что рядом с Брином сидит его жена — Анна Войжитски (Anne Wojcicki), у сестры которой — Сьюзан — Брин и Пейдж, основатели поискового гиганта, снимали гараж, где, согласно легендам, и был создан поисковый алгоритм Google. Сейчас Сьюзан Войжитски является вице-президентом Google по рекламе.    	   	<div class=\"clear\"></div> 	   	</div>      <ul class=\"tags\"> 	<li ><a href=\"http://habrahabr.ru/search/?q=%5Bgoogle%20glass%5D&target_type=posts\" rel=\"tag\">google glass</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5Bgoogle%5D&target_type=posts\" rel=\"tag\">google</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5B%D0%B8%D0%BD%D1%82%D0%B5%D1%80%D1%84%D0%B5%D0%B9%D1%81%D1%8B%5D&target_type=posts\" rel=\"tag\">интерфейсы</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5B%D0%B4%D0%BE%D0%BF%D0%BE%D0%BB%D0%BD%D0%B5%D0%BD%D0%BD%D0%B0%D1%8F%20%D1%80%D0%B5%D0%B0%D0%BB%D1%8C%D0%BD%D0%BE%D1%81%D1%82%D1%8C%5D&target_type=posts\" rel=\"tag\">дополненная реальность</a></li> </ul>      <div class=\"infopanel\" id=\"infopanel_post_145142\"> 	          	 	<div class=\"voting\">  		 		 		<span class=\"plus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		<span class=\"minus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		  		<div class=\"mark\"> 			 			 			 			 			<span class=\"score\" title=\"Результат голосования виден только авторизованным пользователям\">&#8212;</span> 			 			 		</div>  	</div> 	 	    	<div class=\"published\"> 4 июня 2012, 12:17</div>  	<div class=\"favorite\"> 		 		 		<a class=\"guest\"  href=\"#\" onclick=\"return true;\" title=\"Только зарегистрированные пользователи могут добавлять посты в избранное\"></a> 		 	</div>  	<div class=\"favs_count\" title=\"Количество пользователей, добавивших пост в избранное\">25</div>  	 		 		 		<div class=\"twitter\"> 			<a href=\"http://twitter.com/intent/tweet?text=http://habr.ru/p/145142/+%D0%A1%D0%B5%D1%80%D0%B3%D0%B5%D0%B9+%D0%91%D1%80%D0%B8%D0%BD+%D0%BF%D0%BE%D0%BA%D0%B0%D0%B7%D0%B0%D0%BB+Google+Glass+%D0%B2+%D0%A2%D0%92-%D0%B8%D0%BD%D1%82%D0%B5%D1%80%D0%B2%D1%8C%D1%8E+%23habr\" title=\"Опубликовать ссылку в Twitter\" target=\"_blank\"></a> 		</div> 		<div class=\"vkontakte\"> 			<a href=\"http://vkontakte.ru/share.php?url=http://habr.ru/p/145142/\" title=\"Опубликовать ссылку во ВКонтакте\" onclick=\"return true;\"></a> 		</div> 		<div class=\"facebook\"> 			<a href=\"https://www.facebook.com/sharer/sharer.php?u=http://habr.ru/p/145142/\" title=\"Опубликовать ссылку в Facebook\" onclick=\"return true;\"></a> 		</div> 		<div class=\"googleplus\"> 			<a href=\"https://plus.google.com/share?url=http://habr.ru/p/145142/\" title=\"Опубликовать ссылку в Google Plus\" onclick=\"return true;\"></a> 		</div> 		 	  	  	  	 	  	 	 	<div class=\"author\">  		<a title=\"Автор текста\" href=\"http://habrahabr.ru/users/jeston/\" >jeston</a> 		<span class=\"rating\" title=\"рейтинг пользователя\">778,8</span> 		 			<a href=\"https://plus.google.com/110195083688223276717/?rel=author\" class=\"googleplus_profile\">G+</a> 		 	</div> 	 	  	  	  </div> <div class=\"clear\"></div>          	\";}', 0x313333383833303339302E36313532, null, null, '2012-06-04 21:19:50', '7');
INSERT INTO `tbl_content` VALUES ('14', 'a:3:{s:6:\"header\";s:0:\"\";s:5:\"autor\";s:0:\"\";s:7:\"content\";s:14398:\"     <h1 class=\"title\">        	      	                      <span class=\"post_title\">Чипсеты Intel 7-й серии – сравнительный обзор</span>                      			          	</h1>   	   	 <div class=\"hubs\"> 	 		<a href=\"http://habrahabr.ru/hub/hardware/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Железо</a>,  	 		<a href=\"http://habrahabr.ru/company/intel/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Блог компании Intel</a> 	 </div>    	   	<div class=\"content html_format\">   	<img align=\"left\" src=\"/uploads/5b7fc58801dcf97d938bf4d655e15658.jpg\"/>Почти два месяца назад, 9 апреля, Intel официально объявила о выходе чипсетов новой, седьмой серии. И хотя информация об этих чипсетах обсуждалась до того в течение почти что года, думается, имеет смысл еще раз в одном тексте свести воедино все их основные характеристики и функции. Ведь одно дело – просто обсуждать, и совсем другое – после обсуждения пойти в магазин и приобрести выбранное устройство или набор комплектующих.<br/>  Итак, чипсеты седьмой серии (кодовое наименование Panther Point) спроектированы под процессоры нового поколения Ivy Bridge, кроме того, поддерживаются и процессоры предыдущего поколения Sandy Bridge. В целом чипсетная картина выглядит так:<br/>  <table>  <tr>  <td>Intel Q75 Express Chipset</td>  <td rowspan=\"3\">Настольные ПК – корпоративный сегмент</td>  </tr>  <tr>  <td>Intel Q77 Express Chipset</td>  </tr>  <tr>  <td>Intel B75 Express Chipset</td>  </tr>  <tr>  <td>Intel Z77 Express Chipset</td>  <td rowspan=\"3\">Настольные ПК – потребительский сегмент</td>  </tr>  <tr>  <td>Intel Z75 Express Chipset</td>  </tr>  <tr>  <td>Intel H77 Express Chipset</td>  </tr>  <tr>  <td>Mobile Intel HM75 Express Chipset</td>  <td rowspan=\"4\">Мобильные ПК – потребительский сегмент</td>  </tr>  <tr>  <td>Mobile Intel HM76 Express Chipset</td>  </tr>  <tr>  <td>Mobile Intel HM77 Express Chipset</td>  </tr>  <tr>  <td>Mobile Intel UM77 Express Chipset</td>  </tr>  <tr>  <td>Mobile Intel QM77 Express Chipset</td>  <td rowspan=\"2\">Мобильные ПК – корпоративный сегмент</td>  </tr>  <tr>  <td>Mobile Intel QS77 Express Chipset</td>  </tr>  </table><a name=\"habracut\"></a>Для начала, выделим общие черты всех чипсетов:<br/>  <ul>  <li>Интегрированное видео на графическом ядре процессора (то есть по сути материнские платы «без видео» ныне отсутствуют как класс, хотя видео как такового на них нет);</li>  <li>Подключение до 3 независимых мониторов;</li>  <li>Интегрированная сетевая карта 10/100/1000Base-T;</li>  <li>Интегрированный звук Intel High Definition Audio;</li>  <li>Память DDR3 (до 1600 МГц).</li>  </ul>А теперь рассмотрим каждое из семейств по отдельности.<br/>  <br/>  <h5>Чипсеты для домашних ПК</h5><br/>  <img src=\"/uploads/6f0ae77bfda9c19c589cfd1e88a21aff.jpg\"/><br/>  Логика построения линеек примерно аналогична той, что была в шестой серии. В классе настольных домашних ПК топовой будет Z-серия с большим разгонным потенциалом. Флагман серии, Z77, как и прежняя «массовая» заряженная модель Z68, сможет удовлетворить нужды большинства требовательных домашних пользователей – ну а для маньяков по-прежнему имеется X79. H77 – рабочая лошадка с целевой аудиторией, кому ехать, а не шашечки.<br/>  Все чипсеты поддерживают технологию Intel Rapid Storage Technology. Слухи о смерти шины PCI в домашних материнских платах оказались несколько преувеличенными – слоты PCI пока присутствуют, однако контроллер PCI вынесен за пределы чипсета и выполнен сторонним производителем – верный признак его скорого выбывания.<br/>  <table>  <tr>  <th></th>  <th>Z77</th>  <th>Z75</th>  <th>H77</th>  </tr>  <tr>  <td>Слоты PCI Express 3.0</td>  <td>1x16 или<br/>  1x8 + 1x8 или<br/>  1x8 + 2x4*</td>  <td>1x16 или<br/>  1x8 + 1x8 или<br/>  1x8 + 2x4</td>  <td>1x16 или<br/>  1x8 + 2x4</td>  </tr>  <tr>  <td>Линии PCI Express 2.0</td>  <td>8</td>  <td>8</td>  <td>8</td>  </tr>  <tr>  <td>Порты SATA2 (SATA3)</td>  <td>4 (2)</td>  <td>4 (2)</td>  <td>4 (2)</td>  </tr>  <tr>  <td>Порты USB2 (USB3)</td>  <td>10 (4)</td>  <td>10 (4)</td>  <td>10 (4)</td>  </tr>  <tr>  <td><a href=\"http://habrahabr.ru/company/intel/blog/143855/\">Intel Smart Response Technology</a></td>  <td>Да</td>  <td>Нет</td>  <td>Да</td>  </tr>  <tr>  <td>Разгон процессора</td>  <td>Да</td>  <td>Да</td>  <td>Нет</td>  </tr>  </table><i>* Здесь и далее второй PCI Express x4 выделен под шину <a href=\"http://habrahabr.ru/post/114456/\">Thunderbolt</a>.</i><br/>  <br/>  <h5>Чипсеты для бизнес ПК</h5><br/>  <img src=\"/uploads/beb8b64f1b8e4eccc4a21ebbc54e2f05.jpg\"/><br/>  В линейке бизнес-чипсетов, как водится, основное внимание отводится средствам удаленного мониторинга и администрирования, а также корпоративной безопасности. Здесь и флагманская ныне технология vPro (о ней мы уже <a href=\"http://habrahabr.ru/company/intel/blog/138377/\">подробно рассказывали</a>), и Intel Standard Manageability (ISM) и Intel Small Business Advantage (SBA). Младший в линейке чипсет B75 позиционируется как решение для малого и среднего бизнеса, более дорогие аналоги предлагается использовать в крупных корпоративных сетях с развитым централизованным управлением IT-инфраструктурой.<br/>  В отличие от домашних чипсетов, корпоративные пока содержат в себе «родной» контроллер PCI, но, думается, это тоже ненадолго.<br/>  <table>  <tr>  <th></th>  <th>Q77</th>  <th>Q75</th>  <th>B75</th>  </tr>  <tr>  <td>Линии PCI Express 2.0</td>  <td>8</td>  <td>8</td>  <td>8</td>  </tr>  <tr>  <td>Порты SATA2 (SATA3)</td>  <td>4 (2)</td>  <td>5 (1)</td>  <td>5 (1)</td>  </tr>  <tr>  <td>Порты USB2 (USB3)</td>  <td>10 (4)</td>  <td>10 (4)</td>  <td>8 (4)</td>  </tr>  <tr>  <td>Intel Smart Response Technology</td>  <td>Да</td>  <td>Нет</td>  <td>Нет</td>  </tr>  <tr>  <td>Технологии управления и мониторинга</td>  <td>vPro/ISM/SBA</td>  <td>ISM</td>  <td>SBA</td>  </tr>  </table><br/>  <h5>Чипсеты для мобильных ПК</h5><br/>  <img src=\"/uploads/8e30c585f9b38428923950ec800c6290.jpg\"/><br/>  В категории мобильных чипсетов наиболее богатым функционалом обладает HM77, а вот UM77, напротив, чипсет с уменьшенным функционалом, но пониженным энергопотреблением. В целом можно сказать, что мобильные чипсеты в несколько уменьшенном виде наследуют возможности и технологии своих десктопных аналогов, отражая, естественно, нюансы мобильности.<br/>  <table>  <tr>  <th></th>  <th>HM75</th>  <th>HM76</th>  <th>HM77</th>  <th>UM77</th>  <th>QM77</th>  <th>QS77</th>  </tr>  <tr>  <td>Линии PCI Express 2.0</td>  <td>8</td>  <td>8</td>  <td>8</td>  <td>4</td>  <td>8</td>  <td>8</td>  </tr>  <tr>  <td>Порты SATA2 (SATA3)</td>  <td>4 (2)</td>  <td>4 (2)</td>  <td>4 (2)</td>  <td>3 (1)</td>  <td>4 (2)</td>  <td>4 (2)</td>  </tr>  <tr>  <td>Порты USB2 (USB3)</td>  <td>12 (0)</td>  <td>8 (4)</td>  <td>10 (4)</td>  <td>6 (4)</td>  <td>10 (4)</td>  <td>10 (4)</td>  </tr>  <tr>  <td>Intel Rapid Storage</td>  <td>Нет</td>  <td>Нет</td>  <td>Да</td>  <td>Да</td>  <td>Да</td>  <td>Да</td>  </tr>  </table>В список опциональных возможностей мобильных чипсетов также входят:<br/>  <ul>  <li><a href=\"http://habrahabr.ru/company/intel/blog/137529/\">Intel Anti-theft Technology</a> (защита от «угона» ноутбука);</li>  <li>Intel Rapid Start Technology (расширенный режим гибернации и ускорение выхода из нее);</li>  <li>Intel Smart Connect Technology (поддержка сетевой активности во время «сна»).</li>  </ul><br/>  <h5>Сухой остаток</h5><br/>  Итак, что Intel имеет честь нам предложить в седьмом семействе? Существенная «фишка», пожалуй, только одна – PCI Express 3.0. Менее существенная, но весьма приятная – родной, а не навесной USB 3.0. Всё остальное практически без изменений перекочевало, как минимум, из шестой серии. Много это или мало? Скажем, для перехода с того же Intel 6х – скорее всего, недостаточно. Однако не будем забывать, что чипсет — это лишь приятное дополнение к процессору. Если новое семейство процессоров Ivy Bridge будет столь же популярным, как предыдущее, то чипсетам Intel 7x забудется и отсутствие прироста по линии памяти, и всего лишь 2 порта скоростного SATA.<br/>  Так что будущее седьмой серии чипсетов целиком и полностью зависит от того, насколько приглянется пользователям новый «плющевый мост» от Intel.    	   	<div class=\"clear\"></div> 	   	</div>      <ul class=\"tags\"> 	<li ><a href=\"http://habrahabr.ru/search/?q=%5B%D1%87%D0%B8%D0%BF%D1%81%D0%B5%D1%82%D1%8B%5D&target_type=posts\" rel=\"tag\">чипсеты</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5Bivy%20bridge%5D&target_type=posts\" rel=\"tag\">ivy bridge</a></li> </ul>      <div class=\"infopanel\" id=\"infopanel_post_144545\"> 	          	 	<div class=\"voting\">  		 		 		<span class=\"plus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		<span class=\"minus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		  		<div class=\"mark\"> 			 			 			 			 			<span class=\"score\" title=\"Результат голосования виден только авторизованным пользователям\">&#8212;</span> 			 			 		</div>  	</div> 	 	    	<div class=\"published\"> 4 июня 2012, 08:04</div>  	<div class=\"favorite\"> 		 		 		<a class=\"guest\"  href=\"#\" onclick=\"return true;\" title=\"Только зарегистрированные пользователи могут добавлять посты в избранное\"></a> 		 	</div>  	<div class=\"favs_count\" title=\"Количество пользователей, добавивших пост в избранное\">49</div>  	 		 		 		<div class=\"twitter\"> 			<a href=\"http://twitter.com/intent/tweet?text=http://habr.ru/p/144545/+%D0%A7%D0%B8%D0%BF%D1%81%D0%B5%D1%82%D1%8B+Intel+7-%D0%B9+%D1%81%D0%B5%D1%80%D0%B8%D0%B8+%E2%80%93+%D1%81%D1%80%D0%B0%D0%B2%D0%BD%D0%B8%D1%82%D0%B5%D0%BB%D1%8C%D0%BD%D1%8B%D0%B9+%D0%BE%D0%B1%D0%B7%D0%BE%D1%80+%23habr\" title=\"Опубликовать ссылку в Twitter\" target=\"_blank\"></a> 		</div> 		<div class=\"vkontakte\"> 			<a href=\"http://vkontakte.ru/share.php?url=http://habr.ru/p/144545/\" title=\"Опубликовать ссылку во ВКонтакте\" onclick=\"return true;\"></a> 		</div> 		<div class=\"facebook\"> 			<a href=\"https://www.facebook.com/sharer/sharer.php?u=http://habr.ru/p/144545/\" title=\"Опубликовать ссылку в Facebook\" onclick=\"return true;\"></a> 		</div> 		<div class=\"googleplus\"> 			<a href=\"https://plus.google.com/share?url=http://habr.ru/p/144545/\" title=\"Опубликовать ссылку в Google Plus\" onclick=\"return true;\"></a> 		</div> 		 	  	  	  	 	  	 	 	<div class=\"author\">  		<a title=\"Автор текста\" href=\"http://habrahabr.ru/users/saul/\" >saul</a> 		<span class=\"rating\" title=\"рейтинг пользователя\">82,6</span> 		 	</div> 	 	  	  	  </div> <div class=\"clear\"></div>         <script type=\"text/javascript\" src=\"http://habrahabr.ru/core/viewcount/144545/\"></script> 	\";}', 0x313333383833303339372E30363236, null, null, '2012-06-04 21:19:57', '7');
INSERT INTO `tbl_content` VALUES ('15', 'a:3:{s:6:\"header\";s:0:\"\";s:5:\"autor\";s:0:\"\";s:7:\"content\";s:5982:\"     <h1 class=\"title\">        	      	                      <span class=\"post_title\">В штатовской военной микросхеме китайского производства обнаружили запароленный «чёрный ход»</span>                      			          	</h1>   	   	 <div class=\"hubs\"> 	 		<a href=\"http://habrahabr.ru/hub/infosecurity/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Информационная безопасность</a><span class=\"profiled_hub\" title=\"Профильный хаб\">*</span> 	 </div>    	   	<div class=\"content html_format\">   	Прочёл на сайте журнала «Хакер» две статьи:<br/>  <br/>  <ul>  <li><a href=\"http://xakep.ru/post/58757/\">В военной микросхеме США китайского производства обнаружен бэкдор</a><br/>   </li>  <li><a href=\"http://xakep.ru/post/58765/\">Подробности о китайском бэкдоре в чипе FPGA</a></li>  </ul><br/>  В первой статье пишут: исследователям удалось обнаружить в китайском чипе FPGA бэкдор, размещённый производителем и способный снять криптографическую защиту с микросхемы, поменять ключ AES, получить доступ к нешифрованным данным, вывести чип из строя. Удалось узнать и секретный ключ, которым активировался бэкдор. И пишут, что этот чип широко используется в военных системах (включая вооружение), на атомных станциях, на транспорте.<br/>  <br/>  Во второй статье пишут: такие бэкдоры — обычное дело, создаются для отладки аппаратного обеспéчения; часть производителей перед выпуском оборудования удаляет бэкдор, а другая часть оставляет, просто запароливая тайным ключом.<br/>  <br/>  Советую и вам также прочесть ту и другую статью, сделать выводы.    	   	<div class=\"clear\"></div> 	   	</div>      <ul class=\"tags\"> 	<li ><a href=\"http://habrahabr.ru/search/?q=%5B%D0%B1%D1%8D%D0%BA%D0%B4%D0%BE%D1%80%5D&target_type=posts\" rel=\"tag\">бэкдор</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5B%D0%B1%D1%8D%D0%BA%D0%B4%D0%BE%D1%80%D1%8B%5D&target_type=posts\" rel=\"tag\">бэкдоры</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5B%D0%BA%D0%B8%D1%82%D0%B0%D0%B9%D1%81%D0%BA%D0%B8%D0%B5%20%D0%BF%D0%BE%D0%B4%D0%B5%D0%BB%D0%BA%D0%B8%5D&target_type=posts\" rel=\"tag\">китайские поделки</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5B%D0%B0%D1%80%D0%BC%D0%B8%D1%8F%20%D0%A1%D0%A8%D0%90%5D&target_type=posts\" rel=\"tag\">армия США</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5BFPGA%5D&target_type=posts\" rel=\"tag\">FPGA</a></li> </ul>      <div class=\"infopanel\" id=\"infopanel_post_145172\"> 	          	 	<div class=\"voting\">  		 		 		<span class=\"plus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		<span class=\"minus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		  		<div class=\"mark\"> 			 			 			 			 			<span class=\"score\" title=\"Результат голосования виден только авторизованным пользователям\">&#8212;</span> 			 			 		</div>  	</div> 	 	    	<div class=\"published\"> 4 июня 2012, 16:01</div>  	<div class=\"favorite\"> 		 		 		<a class=\"guest\"  href=\"#\" onclick=\"return true;\" title=\"Только зарегистрированные пользователи могут добавлять посты в избранное\"></a> 		 	</div>  	<div class=\"favs_count\" title=\"Количество пользователей, добавивших пост в избранное\">22</div>  	 		 		 		<div class=\"twitter\"> 			<a href=\"http://twitter.com/intent/tweet?text=http://habr.ru/p/145172/+%D0%92%C2%A0%D1%88%D1%82%D0%B0%D1%82%D0%BE%D0%B2%D1%81%D0%BA%D0%BE%D0%B9+%D0%B2%D0%BE%D0%B5%D0%BD%D0%BD%D0%BE%D0%B9+%D0%BC%D0%B8%D0%BA%D1%80%D0%BE%D1%81%D1%85%D0%B5%D0%BC%D0%B5+%D0%BA%D0%B8%D1%82%D0%B0%D0%B9%D1%81%D0%BA%D0%BE%D0%B3%D0%BE+%D0%BF%D1%80%D0%BE%D0%B8%D0%B7%D0%B2%D0%BE%D0%B4%D1%81%D1%82%D0%B2%D0%B0+%D0%BE%D0%B1%D0%BD%D0%B0%D1%80%D1%83%D0%B6%D0%B8%D0%BB%D0%B8+%D0%B7%D0%B0%D0%BF%D0%B0%D1%80%D0%BE%D0%BB%D0%B5%D0%BD%D0%BD%D1%8B%D0%B9+%C2%AB%D1%87%D1%91%D1%80%D0%BD%D1%8B%D0%B9%C2%A0%D1%85%D0%BE%D0%B4%C2%BB+%23habr\" title=\"Опубликовать ссылку в Twitter\" target=\"_blank\"></a> 		</div> 		<div class=\"vkontakte\"> 			<a href=\"http://vkontakte.ru/share.php?url=http://habr.ru/p/145172/\" title=\"Опубликовать ссылку во ВКонтакте\" onclick=\"return true;\"></a> 		</div> 		<div class=\"facebook\"> 			<a href=\"https://www.facebook.com/sharer/sharer.php?u=http://habr.ru/p/145172/\" title=\"Опубликовать ссылку в Facebook\" onclick=\"return true;\"></a> 		</div> 		<div class=\"googleplus\"> 			<a href=\"https://plus.google.com/share?url=http://habr.ru/p/145172/\" title=\"Опубликовать ссылку в Google Plus\" onclick=\"return true;\"></a> 		</div> 		 	  	  	  	 	  	 	 	<div class=\"author\">  		<a title=\"Автор текста\" href=\"http://habrahabr.ru/users/Mithgol/\" >Mithgol</a> 		<span class=\"rating\" title=\"рейтинг пользователя\">146,4</span> 		 	</div> 	 	  	  	  </div> <div class=\"clear\"></div>          	\";}', 0x313333383833303339392E30323037, null, null, '2012-06-04 21:19:59', '7');
INSERT INTO `tbl_content` VALUES ('16', 'a:3:{s:6:\"header\";s:0:\"\";s:5:\"autor\";s:0:\"\";s:7:\"content\";s:17531:\"     <h1 class=\"title\">        	      	                      <span class=\"post_title\">В чем причина засора? Cтатистика веб-приложений</span>                      			          	</h1>   	   	 <div class=\"hubs\"> 	 		<a href=\"http://habrahabr.ru/hub/hi/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Высокая производительность</a><span class=\"profiled_hub\" title=\"Профильный хаб\">*</span>,  	 		<a href=\"http://habrahabr.ru/hub/webdev/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Веб-разработка</a><span class=\"profiled_hub\" title=\"Профильный хаб\">*</span>,  	 		<a href=\"http://habrahabr.ru/company/mamba/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >Блог компании Мамба</a> 	 </div>    	   	<div class=\"content html_format\">   	В прошлом нашем посте внимательный хабраюзер <a href=\"http://habrahabr.ru/users/dovg/\" class=\"user_link\">dovg</a> отметил скриншот с красивым графиком. На нем было отражено время выполнения различных операций поиска. А поскольку статистика и анализ производительности высоконагруженных проектов – тема довольно актуальная, мы решили рассказать про систему, которую используем для сбора и анализа статистики «Мамбы». Как и в случае поиска, мы используем собственное решение, но в отличие от него BTP (никто не помнит, как эта аббревиатура появилась на свет, но почему-то именно она стала названием) находится в открытом доступе, и при желании вы можете установить её на своих серверах.<br/>  <br/>  <img src=\"/uploads/0d0c42fd634f9d44163146f93ce96e2f.png\" alt=\"image\"/><br/>  <a name=\"habracut\"></a><br/>  Система выдает следующие виды статистики:<br/>  <ul>  <li>по операциям на сервисе (например, «mysql» или «memcache»)</li>  <li>по операциям на сервере конкретного сервиса (например, сервис mysql и сервер db7)</li>  <li>по скриптам, которые используют какую-то операцию сервиса (например, «update» сервиса «mysql»)</li>  <li>по сервисам/операциям, которые использует конкретный скрипт</li>  </ul><br/>  Чтобы получить наиболее полную картину работы приложений, мы использовали представление информации в двух иерархиях. Первая вполне традиционна: сервис-сервер-операция. Она позволяет определить нагрузку на сервисы, равномерность распределения нагрузки по серверам и частоту использования конкретных операций.<br/>  <br/>  Здесь отражается, например, количество запросов к кластеризованным БД. Контроль времени отклика каждой из них позволяет понять эффективность работы в целом, а в случае проблем, выяснить какая конкретно база тормозит больше всех. В этой же иерархии мы смотрим, возросли только селекты или также и апдейты; нормальна или повышена нагрузка на кэш и т.п. Иными словами, в случае проблем здесь мы определяем точку отказа — какой сервис/сервер снижает общую производительность. При этом, обрабатывая информацию, веб-интерфейс берёт также данные и из второй иерархии, отображая, какие скрипты больше всего используют интересующую нас операцию какого-либо определенного сервиса. <br/>  <br/>  Вторую иерархию — скрипт-сервис-операция — можно использовать, чтобы выяснить конкретную “причину засора”, а именно: к чему тормозящий скрипт обращается и на что тратит время. В этой иерархии можно определить, какие конкретно сервисы скрипт использует, сколько запросов делает и каково время ответа на эти запросы. Именно так можно, например, определить, почему изменение кода скрипта привело к росту нагрузки на определённую БД. <br/>  <br/>  <img src=\"/uploads/16dff4a62e74e45a23391714977fbe11.png\"/><br/>  <br/>  Наиболее распространенный показатель производительности веб-приложений — время отклика. Многие системы ограничивают аналитику средним и пограничными временем отклика, но на практике эти данные могут оказаться “сферическим конем в вакууме”. Предположим, что 10% посетителей получают нужную страницу в 10-20 раз медленнее остальных 90%. Это не скажется сильно на среднем показателе, однако при миллионной аудитории выльется в сотни тысяч недовольных пользователей. Поэтому мы используем отчеты по перцентилям и можем посмотреть не только средние показатели, но и значения, в которые укладываются 50, 80, 95 и 99 процентов операций. Это позволяет нам получить намного более релевантный результат с точки зрения “удовлетворенности пользователей”.<br/>  <br/>  <a href=\"/uploads/35c512de2841e4f9c4993715d1530c79.png\"><img src=\"/uploads/35c512de2841e4f9c4993715d1530c79.png\" title=\"Система статистики BTP\"/></a><br/>  <br/>  Разрабатывая систему, мы сразу предполагали, что количество и состав измеряемых параметров могут меняться. Кроме того, долговременная аналитика невозможна без анализа сохраненных за какой-то промежуток времени данных, поэтому хранению имеющейся статистики мы тоже уделили отдельное внимание. <br/>  <br/>  В результате с технической точки зрения система состоит из следующих частей:<br/>  <ol>  <li><b>Демон</b><br/>  Принимает и обрабатывает данные счётчиков; считает, хранит и выдаёт статистику. Работает по протоколу JSON-RPC, а для хранения использует библиотеку kyoto cabinet (встраиваемое key-value хранилище).</li>  <li><b>Веб-интерфейс</b><br/>  Собственно интерфейс просмотра статистики. Рисует графики (подробнее о графиках ниже).</li>  <li><b>Клиент</b><br/>  Это та часть, которая интегрируется в ваше приложение. Отправляет счётчики в демон. При этом можно использовать уже готовый клиент на PHP или Python, а также, при желании, написать свой собственный.</li>  </ol><br/>  Когда вы заходите в веб-интерфейс, то сразу видите статистику по счётчикам из вашего скрипта. Статистика за последние ~2 часа обсчитывается с разрешением в 5 секунд. Сутки — с разрешением в 1 минуту (ну и есть, естественно, статистика за месяц и год с разрешением в 30 минут и в 6 часов соответственно).<br/>  <br/>  Для рисования графиков веб-интерфейс использует клиентские шаблоны, Backbone.js и dygraphs и почти весь написан на javascript, за исключением маленького http-to-json-rpc-прокси файлика js.php, и index.php (который собирает на страницу клиентские шаблоны из отдельной директории).<br/>  <br/>  Собственно на графиках стоит остановиться отдельно. Для их генерации и вывода мы пробовали использовать несколько разных библиотек, в том числе highcharts, raphael.js и envision.js, но остановились на dygraphs. Рассуждали мы примерно следующим образом:<br/>  <br/>  <b>Raphael:</b> svg-based библиотека, в которой практически всё приходится делать руками. В том числе чертить координатную сетку в виде отрезков SVG. И при этом она не отличается высокой скоростью.<br/>  <b>Highcharts: </b>сильно проще, чем Raphael, тоже svg-based, но при кастомизации появляются свои сложности. И тоже не очень быстрая (3-4 графика по 1.5 тысячи точек ощутимо тормозят).<br/>  <b>Envision.js:</b> canvas-based, чуть шустрее (если не использовать всякие интересные штуки типа мини-навигации). В целом неплохой вариант.<br/>  <b>Dygraphs: </b>самый шустрый. Имеющийся функционал реализуется относительно просто, однако некоторых возможностей не хватает (например, не получилось сделать вывод на одном экране двух графиков в разных представлениях: в виде закрашенной области и простой линией). Тем не менее, скорость работы в разы превысила таковую у остальных библиотек. В результате выбор был остановлен именно на Dygraphs.<br/>  <br/>  Естественно, существуют и другие системы сбора статистики, например, New Relic и Pinba. Но BTP имеет несколько выгодных отличий.<br/>  <br/>  От New Relic она отличается тем, что, во-первых, является открытым продуктом, а во-вторых, ориентирована именно на мониторинг параметров приложения (то, что в new relic называется custom metrics). Ну и в третьих, не нужна установка php extension и java proxy agent. Хотя стоит признать, что интерфейс у New Relic более “вылизан”. <br/>  <br/>  От Pinba наша система отличается, во-первых, более простой установкой (не нужен плагин к MySQL, не нужен extension), а во-вторых, тем, что пинба занимается только подсчётом статистики. Pinba не хранит и не отображает данные, а чтобы это делать, к Пинбе приходится прикручивать крон-скрипты, rrdtool с кучей параметров командной строки и средства для рисования графиков. И естественно, при добавлении новых счётчиков нужно не забыть сохранять данные и с них тоже. <br/>  <br/>  Ссылки:<br/>  BTP/ Демон<br/>  <a href=\"https://github.com/mambaru/btp-daemon\">https://github.com/mambaru/btp-daemon</a><br/>  <a href=\"http://imgur.com/a/idFF6\">Картинки</a><br/>  <br/>  BTP/Веб-интерфейс <br/>  <a href=\"https://github.com/mambaru/btp-webui\">https://github.com/mambaru/btp-webui</a><br/>  <br/>  BTP/Наш клиент<br/>  <a href=\"https://github.com/mambaru/btp-api\">https://github.com/mambaru/btp-api</a><br/>  <br/>  BTP/Python-клиент<br/>  <a href=\"https://github.com/mastergenius/pybtp\">https://github.com/mastergenius/pybtp</a><br/>  <br/>  New Relic<br/>  <a href=\"https://newrelic.com/docs/php/the-php-api\">https://newrelic.com/docs/php/the-php-api</a><br/>  <br/>  Pinba<br/>  <a href=\"http://pinba.org/wiki/Main_Page\">http://pinba.org/wiki/Main_Page</a>    	   	<div class=\"clear\"></div> 	   	</div>      <ul class=\"tags\"> 	<li ><a href=\"http://habrahabr.ru/search/?q=%5B%D1%81%D1%82%D0%B0%D1%82%D0%B8%D1%81%D1%82%D0%B8%D0%BA%D0%B0%5D&target_type=posts\" rel=\"tag\">статистика</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5Bperformance%5D&target_type=posts\" rel=\"tag\">performance</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5Bbtp%5D&target_type=posts\" rel=\"tag\">btp</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5Bpinba%5D&target_type=posts\" rel=\"tag\">pinba</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5Bnew%20relic%5D&target_type=posts\" rel=\"tag\">new relic</a></li> </ul>      <div class=\"infopanel\" id=\"infopanel_post_144910\"> 	          	 	<div class=\"voting\">  		 		 		<span class=\"plus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		<span class=\"minus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		  		<div class=\"mark\"> 			 			 			 			 			<span class=\"score\" title=\"Результат голосования виден только авторизованным пользователям\">&#8212;</span> 			 			 		</div>  	</div> 	 	    	<div class=\"published\"> 4 июня 2012, 12:17</div>  	<div class=\"favorite\"> 		 		 		<a class=\"guest\"  href=\"#\" onclick=\"return true;\" title=\"Только зарегистрированные пользователи могут добавлять посты в избранное\"></a> 		 	</div>  	<div class=\"favs_count\" title=\"Количество пользователей, добавивших пост в избранное\">91</div>  	 		 		 		<div class=\"twitter\"> 			<a href=\"http://twitter.com/intent/tweet?text=http://habr.ru/p/144910/+%D0%92+%D1%87%D0%B5%D0%BC+%D0%BF%D1%80%D0%B8%D1%87%D0%B8%D0%BD%D0%B0+%D0%B7%D0%B0%D1%81%D0%BE%D1%80%D0%B0%3F+C%D1%82%D0%B0%D1%82%D0%B8%D1%81%D1%82%D0%B8%D0%BA%D0%B0+%D0%B2%D0%B5%D0%B1-%D0%BF%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D0%B9+%23habr\" title=\"Опубликовать ссылку в Twitter\" target=\"_blank\"></a> 		</div> 		<div class=\"vkontakte\"> 			<a href=\"http://vkontakte.ru/share.php?url=http://habr.ru/p/144910/\" title=\"Опубликовать ссылку во ВКонтакте\" onclick=\"return true;\"></a> 		</div> 		<div class=\"facebook\"> 			<a href=\"https://www.facebook.com/sharer/sharer.php?u=http://habr.ru/p/144910/\" title=\"Опубликовать ссылку в Facebook\" onclick=\"return true;\"></a> 		</div> 		<div class=\"googleplus\"> 			<a href=\"https://plus.google.com/share?url=http://habr.ru/p/144910/\" title=\"Опубликовать ссылку в Google Plus\" onclick=\"return true;\"></a> 		</div> 		 	  	  	  	 	  	 	 	<div class=\"author\">  		<a title=\"Автор текста\" href=\"http://habrahabr.ru/users/Julia_Gryzunova/\" >Julia_Gryzunova</a> 		<span class=\"rating\" title=\"рейтинг пользователя\">95,5</span> 		 	</div> 	 	  	  	  </div> <div class=\"clear\"></div>         <script type=\"text/javascript\" src=\"http://habrahabr.ru/core/viewcount/144910/\"></script> 	\";}', 0x313333383833303430332E30303235, null, null, '2012-06-04 21:20:02', '7');
INSERT INTO `tbl_content` VALUES ('17', 'a:3:{s:6:\"header\";s:0:\"\";s:5:\"autor\";s:0:\"\";s:7:\"content\";s:18538:\"     <h1 class=\"title\">        	      	                      <span class=\"post_title\">Практическая оптимизация и масштабируемость MySQL InnoDB на больших объёмах данных</span>                      			          	</h1>   	   	 <div class=\"hubs\"> 	 		<a href=\"http://habrahabr.ru/hub/mysql/\" class=\"hub\" title=\"Вы не подписаны на этот хаб\" >MySQL</a><span class=\"profiled_hub\" title=\"Профильный хаб\">*</span> 	 </div>    	   	<div class=\"content html_format\">   	Данный пост не будет рассказывать про индексы, планы запросов, триггеры для построения агрегатов и прочие общие способы оптимизации запросов и структуры БД. Так же не будет рассказывать про оптимальные настройки с префиксом innodb_. Возможно прочитав текст ниже вы лучше поймёте смысл некоторых из них. В данном посте речь пойдёт об <b>InnoDB</b> и его функционирование.<br/>  <br/>  <h4>Какие проблемы может помочь решить этот пост?</h4><br/>  <ul>  <li>Что делать если у вас в списке процессов множественные селекты которым казалось бы никто не мешает?</li>  <li>Что делать если всё хорошо настроено, запросы пролетают как ракеты и список процессов постоянно пустой, но на сервере высокий LA и запросы начинают работать немного медленнее, ну например вместо 100мс получается 500мс ?</li>  <li>Как быстро масштабировать систему, когда нет возможности всё переделать?</li>  <li>У вас коммерческий проект в конкурентной среде и проблему надо решать немедленно? </li>  <li>Почему один и тот же запрос работает то быстро то медленно?</li>  <li>Как организовать быстрый кеш и поддерживать его в актуальном состояние?</li>  </ul><br/>  <a name=\"habracut\"></a><br/>  <h4>Как обычно выглядит работа с БД</h4><br/>  Приблизительно схема работы обычно такая<br/>  <ol>  <li>Запрос</li>  <li>План запроса</li>  <li>Поиск по индексу</li>  <li>Получение данных из таблиц</li>  <li>Отправка данных клиенту</li>  </ol><br/>  Даже если вы не делали <b>start transaction</b> каждый ваш отдельный запрос будет являться по сути транзакцией из одного запроса. Как известно у транзакций есть <b>уровень изолированности</b> который у MySQL по умолчанию <b>REPEATABLE READ</b>. А что это значит для нас? А то, что когда вы в транзакции «касаетесь любой таблицы» её версия на тот момент фиксируется и вы перестаёте видеть изменения сделанные в других транзакциях. Чем длиннее ваш запрос или транзакция, тем больше «старых» данных продолжает накапливать MySQL и есть основания предполагать, что происходит это с активным использованием основного пула памяти. Т.е. каждый ваш безобидный селект объединяющий 10 таблиц по первичному ключу, в случае активной работы с БД, начинает иметь достаточно тяжёлый побочный эффект. У PostgreSQL как и у Oracle уровень изолированности по умолчанию <b>READ COMMITTED</b> который функционирует гораздо проще, чем <b>REPEATABLE READ</b>. Само собой с уровнем изоляции <b>READ COMMITTED</b> вам придётся использовать <b>построчную репликацию</b>. Вы можете легко проверить уровни изолированности транзакций просто подключивших двумя клиентами к БД и поделать select, delete и update на одной и той же таблице. Это и есть ответ на вопрос про зависающие селекты, попробуйте сменить уровень изоляции БД, это может вам помочь.<br/>  <br/>  <h5>Как InnoDB работает с данными</h5><br/>  InnoDB хранит данные на жёстком диске в страницах. При обращение к нужной странице, она загружается в оперативную память и затем уже с ней происходят различные действия, будь то чтение запись или что-то ещё. Вот именно этой памятью и является Innodb_buffer_pool размер которого вы выставляете в innodb_buffer_pool_size. Схема работы вполне классическая и ничего необычного в ней нет. Посмотреть отчёты о работе InnoDB можно следующим образом:<br/>  <br/>  <code>SHOW VARIABLES like \'Innodb%\';<br/>  SHOW GLOBAL STATUS like \'Innodb%\';<br/>  SHOW ENGINE INNODB STATUS;<br/>  </code> <br/>  <br/>  Итого мы получаем следующие временные затраты на выполнение операции чтения или записи в БД<br/>  <ol>  <li>Время на загрузка данных в память с жёсткого диска</li>  <li>Время на обработку данных в памяти</li>  <li>Время на запись данных на жёсткий диск если это требуется (тут стоит отметить, что не все данные сразу пишутся на диск, главное чтобы они были зафиксированы в журнале)</li>  </ol><br/>  <br/>  Думаю интуитивно понятно, что если данные находятся в пуле InnoDB в момент запроса, то с диска они не загружаются, что сильно уменьшает время выполнения любых операций с БД. <br/>  <br/>  Самый быстрый вариант работы БД это когда все данные и индексы легко помешаются в пул и по сути БД всегда работает из памяти. В версии 5.6.5 даже есть возможность сохранять весь пул на диск при перезапуске БД, что позволяет избежать холодного старта. <br/>  <br/>  Теперь давайте рассмотрим немного другой вариант событий, когда объём данных на диске превышает размер пула памяти. Пусть размер пула у нас будет 4 страницы выглядеть это будет как [0,0,0,0] и 16 страниц данных 1..16 соответственно. Пользователи у нас чаще всего запрашивают страницы 15 и 16 т.к. в них самые свежие данные и они всегда находятся в памяти. Очевидно, что работает всё так же быстро как и в случае описанном выше.<br/>  <br/>  Ну и неудачный вариант, когда у вас есть 2 страницы активно запрашиваемые пользователями и 8 страниц, которые постоянно используются внутренними скриптами и различными демонами. Таким образом за 4 странице в буфере постоянно идёт борьба которая превращается в вечное чтение с диска и замедление работы системы для пользователей т.к. демоны как правило гораздо активнее себя ведут.<br/>  <br/>  В таком режиме вам может помочь настройка репликации с ещё одни МySQL сервером который может принять на себя часть запросов и снизить борьбу за пул памяти. Но как известно у репликации в MySQL есть существенный недостаток, а именно применение изменений в 1 поток. Т.е. при определённых условиях слейв у вас или начнёт отставать или даст совсем несущественный прирост производительности. В этой ситуации может помочь возможность создавать слейвов с ограниченным числом таблиц. Что позволит получить выигрыш как на применение изменений так и на использование пула памяти. Во многих случаях, когда известны данные которые пользователи запрашивают чаще всего, вы можете создать для них кеш хранящий данные только из необходимых таблиц. В удачном случае у вас получится кеш автоматически поддерживающий свою актуальность. Для тех кому интересно как быстро создать ещё одного слейва, предлагаю посмотреть в сторону <br/>  <br/>  <code>STOP SLAVE;<br/>  SHOW SLAVE STATUS;//Master_Log_File и Exec_Master_Log_Pos<br/>  Тут нужно сделать дамп таблиц которые вам понадобятся<br/>  START SLAVE;<br/>  </code><br/>  <br/>  После того как вставите данные в новый слейв сервер, нужно будет только сделать <br/>  <br/>  <code>CHANGE MASTER TO ... ;<br/>  START SLAVE;<br/>  </code><br/>  <br/>  Всё конечно зависит от объёма данных, но как правило поднять такого слейва можно достаточно быстро.<br/>  <br/>  <h5>Декомпозиция системы на модули используя частичную репликацию</h5><br/>  И так мы можем создавать частичные реплики основной БД, что позволяет нам контролировать распределение памяти между определёнными группами данных. Какие возможности это перед нам открывает? <br/>  Как вы можете выяснить опытным путём никто не мешает вам создавать свои таблицы на слейв сервере и даже создавать в них внешние ключи на реплицируемые данные. Т.е. вы можете иметь не только целостную основную БД, но и целостных слейвов с расширенным набором таблиц. Например, ваша главная БД содержит таблицу <i>users</i> и различные вспомогательные таблицы типа <i>payments</i>. Так же у вас есть сервис блогов, который позволяет пользователям писать сообщения. Вы реплицируете <i>users</i> в другую БД, в которой создаёте таблицу <i>posts</i>. Если на БД содержащую таблицу <i>posts</i> выпадает высокая нагрузка на чтение, вы создаёте реплики содержащие таблицы <i>users</i> и <i>posts</i>. Таким образом можно производить декомпозицию пока объём данных необходимого набора таблицы не станет превышать разумные пределы. В этом случае уже стоит посмотреть в сторону шардинга огромных таблиц, например по хешу идентификатора пользователя, а запросы к нужным воркерам направлять через MQ.<br/>  <br/>  <h5>Итоги</h5><br/>  MySQL предоставляет простой механизм репликации просто пишущий данные в указанные таблицы. Это и даёт широкие возможности по разворачиванию дополнительных сервисов содержащих целостные части БД.<br/>  <br/>  <b>UPD.</b> В случае большого количества мелких транзакций в БД можно попробовать изменить значение innodb_flush_log_at_trx_commit, это обеспечит уменьшение нагрузки на жёсткий диск, но будьте внимательны! <b> теоретически это может привести к потере данных</b>. Увидеть результат работы этой переменной достаточно легко, т.к. можно установить её через SET GLOBAL.<br/>  <br/>  <b> UPD2. </b> Удачная декомпозиция системы позволяет варьировать настройки отдельных серверов в зависимости от выполняемых ими задач. Например на сервере платежей можно иметь SERIALIZABLE и innodb_flush_log_at_trx_commit = 1. А на сервере постов READ COMMITTED и innodb_flush_log_at_trx_commit = 2. Всё зависит от выполняемых задач, критичности данных и вероятности сбоя.    	   	<div class=\"clear\"></div> 	   	</div>      <ul class=\"tags\"> 	<li ><a href=\"http://habrahabr.ru/search/?q=%5BMySQL%5D&target_type=posts\" rel=\"tag\">MySQL</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5BHighLoad%5D&target_type=posts\" rel=\"tag\">HighLoad</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5BReplication%5D&target_type=posts\" rel=\"tag\">Replication</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5Bmysql%20performance%5D&target_type=posts\" rel=\"tag\">mysql performance</a></li><li >, <a href=\"http://habrahabr.ru/search/?q=%5Binnodb%5D&target_type=posts\" rel=\"tag\">innodb</a></li> </ul>      <div class=\"infopanel\" id=\"infopanel_post_142530\"> 	          	 	<div class=\"voting\">  		 		 		<span class=\"plus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		<span class=\"minus\" title=\"Голосовать могут только зарегистрированные пользователи.\"></span> 		  		<div class=\"mark\"> 			 			 			 			 			<span class=\"score\" title=\"Результат голосования виден только авторизованным пользователям\">&#8212;</span> 			 			 		</div>  	</div> 	 	    	<div class=\"published\"> 4 июня 2012, 07:10</div>  	<div class=\"favorite\"> 		 		 		<a class=\"guest\"  href=\"#\" onclick=\"return true;\" title=\"Только зарегистрированные пользователи могут добавлять посты в избранное\"></a> 		 	</div>  	<div class=\"favs_count\" title=\"Количество пользователей, добавивших пост в избранное\">187</div>  	 		 		 		<div class=\"twitter\"> 			<a href=\"http://twitter.com/intent/tweet?text=http://habr.ru/p/142530/+%D0%9F%D1%80%D0%B0%D0%BA%D1%82%D0%B8%D1%87%D0%B5%D1%81%D0%BA%D0%B0%D1%8F+%D0%BE%D0%BF%D1%82%D0%B8%D0%BC%D0%B8%D0%B7%D0%B0%D1%86%D0%B8%D1%8F+%D0%B8+%D0%BC%D0%B0%D1%81%D1%88%D1%82%D0%B0%D0%B1%D0%B8%D1%80%D1%83%D0%B5%D0%BC%D0%BE%D1%81%D1%82%D1%8C+MySQL+InnoDB+%D0%BD%D0%B0+%D0%B1%D0%BE%D0%BB%D1%8C%D1%88%D0%B8%D1%85+%D0%BE%D0%B1%D1%8A%D1%91%D0%BC%D0%B0%D1%85+%D0%B4%D0%B0%D0%BD%D0%BD%D1%8B%D1%85+%23habr\" title=\"Опубликовать ссылку в Twitter\" target=\"_blank\"></a> 		</div> 		<div class=\"vkontakte\"> 			<a href=\"http://vkontakte.ru/share.php?url=http://habr.ru/p/142530/\" title=\"Опубликовать ссылку во ВКонтакте\" onclick=\"return true;\"></a> 		</div> 		<div class=\"facebook\"> 			<a href=\"https://www.facebook.com/sharer/sharer.php?u=http://habr.ru/p/142530/\" title=\"Опубликовать ссылку в Facebook\" onclick=\"return true;\"></a> 		</div> 		<div class=\"googleplus\"> 			<a href=\"https://plus.google.com/share?url=http://habr.ru/p/142530/\" title=\"Опубликовать ссылку в Google Plus\" onclick=\"return true;\"></a> 		</div> 		 	  	  	  	 	  	 	 	<div class=\"au